<script>
// import CalendarComponent from "@/components/ReservationsComponent.vue";
import NavComponent from "@/components/NavComponent.vue";

export default {
    components:{
        // CalendarComponent,
        NavComponent,
    },
    data() {
        return {
            scrollYCoord: 0,
            firstServiceTop: 0,
        }
    },
    created() {
        window.addEventListener('scroll',() => {
            this.firstServiceTop = document.getElementsByClassName("service")[0].getBoundingClientRect().top;
            this.displayPrice();
        })
    },
    methods: {
        displayPrice(){
            if (this.firstServiceTop < 220 && this.firstServiceTop > -440) {
                this.$refs.titleH1.innerHTML = "Hajvágás"
                this.$refs.daniPrice.innerHTML = "<span style='color: #5800ff'>5500</span> Ft"
                this.$refs.markPrice.innerHTML = "<span style='color: #5800ff'>5500</span> Ft"
                this.$refs.erikPrice.innerHTML = "<span style='color: #5800ff'>6000</span> Ft"
                this.$refs.milanPrice.innerHTML = "<span style='color: #5800ff'>5000</span> Ft"
                this.$refs.titleLine.style.backgroundColor = "#5800ff"
            } else if (this.firstServiceTop < -440 && this.firstServiceTop > -1100) {
                this.$refs.titleH1.innerHTML = "Hosszú Hajvágás"
                this.$refs.daniPrice.innerHTML = "<span style='color: #ffc600'>6500</span> Ft"
                this.$refs.markPrice.innerHTML = "<span style='color: #ffc600'>6500</span> Ft"
                this.$refs.erikPrice.innerHTML = "<span style='color: #ffc600'>6500</span> Ft"
                this.$refs.milanPrice.innerHTML = "<span style='color: #ffc600'>6000</span> Ft"
                this.$refs.titleLine.style.backgroundColor = "#ffc600"
            } else if (this.firstServiceTop < -1100 && this.firstServiceTop > -1760) {
                this.$refs.titleH1.innerHTML = "Szakáll Igazítás"
                this.$refs.daniPrice.innerHTML = "<span style='color: #5800ff'>4000</span> Ft"
                this.$refs.markPrice.innerHTML = "<span style='color: #5800ff'>4000</span> Ft"
                this.$refs.erikPrice.innerHTML = "<span style='color: #5800ff'>4500</span> Ft"
                this.$refs.milanPrice.innerHTML = "<span style='color: #5800ff'>4000</span> Ft"
                this.$refs.titleLine.style.backgroundColor = "#5800ff"
            } else if (this.firstServiceTop < -1760 && this.firstServiceTop > -2360) {
                this.$refs.titleH1.innerHTML = "Hajvágás + Szakáll Igazítás"
                this.$refs.daniPrice.innerHTML = "<span style='color: #ffc600'>8000</span> Ft"
                this.$refs.markPrice.innerHTML = "<span style='color: #ffc600'>8000</span> Ft"
                this.$refs.erikPrice.innerHTML = "<span style='color: #ffc600'>8500</span> Ft"
                this.$refs.milanPrice.innerHTML = "<span style='color: #ffc600'>7500</span> Ft"
                this.$refs.titleLine.style.backgroundColor = "#ffc600"
            } else {
                this.$refs.titleH1.innerHTML = "Szolgáltatásaink"
                this.$refs.daniPrice.innerHTML = "0"
                this.$refs.markPrice.innerHTML = "0"
                this.$refs.erikPrice.innerHTML = "0"
                this.$refs.milanPrice.innerHTML = "0"
                this.$refs.titleLine.style.backgroundColor = "#ffc600"
            }
        },
    },
}
</script>

