import NavComponent from "@/components/NavComponent.vue";
import FooterComponent from "@/components/FooterComponent.vue";

export default {
    components:{
        // CalendarComponent,
        NavComponent,
        FooterComponent,
    },
    data() {
        return {
            scrollYCoord: 0,
            firstServiceTop: 0,
            titleH1: "Szolgáltatásaink",
            services: [
                {barber: "Oláh Dani", hair: 5500, longHair: 6500, beard: 4000, hairAndBeard: 8000},
                {barber: "Szabó Márk", hair: 5500, longHair: 6500, beard: 4000, hairAndBeard: 8000},
                {barber: "Szabó Erik", hair: 6000, longHair: 6500, beard: 4500, hairAndBeard: 8500},
                {barber: "Veress Milán", hair: 5000, longHair: 6000, beard: 4000, hairAndBeard: 7500},
            ],
            actualPriceDani: 0,
            actualPriceMark: 0,
            actualPriceErik: 0,
            actualPriceMilan: 0,
            lineBg: "#ffc600",
            textColor: "transparent",
            shownSvg: 0,
            cuts: [
                "C1.jpeg",
                "C2.jpeg",
                "C3.jpeg",
                "C4.jpeg",
                "C5.jpeg",
                "C6.jpeg",
                "C7.jpeg",
                "C8.jpeg",
                "C9.jpeg"
            ],
            outlinePixels: '0px',
        }
    },
    created() {
        window.addEventListener('scroll',() => {
            this.firstServiceTop = document.getElementsByClassName("service")[0].getBoundingClientRect().top;
            this.displayPrice();
        })
    },
    methods: {
        getBackgroundImage(item) {
            return {
                backgroundImage: `url(${require(`@/assets/Cuts/${item}`)})`
            };
        },
        displayPrice(){
            if (this.firstServiceTop + 85 < 420 && this.firstServiceTop + 85 > -440) {
                this.titleH1 = "Hajvágás"
                this.setPrices('hair')
                this.lineBg = "#5800ff"
                this.textColor = "#5800ff"
                this.shownSvg = 1
                this.outlinePixels = "2px"
            } else if (this.firstServiceTop + 85 < -440 && this.firstServiceTop + 85 > -1100) {
                this.titleH1 = "Hosszú Hajvágás"
                this.setPrices('longHair')
                this.lineBg = "#ffc600"
                this.textColor = "#ffc600"
                this.shownSvg = 2
            } else if (this.firstServiceTop + 85 < -1100 && this.firstServiceTop + 85 > -1760) {
                this.titleH1 = "Szakáll Igazítás"
                this.setPrices('beard')
                this.lineBg = "#5800ff"
                this.textColor = "#5800ff"
                this.shownSvg = 3
            } else if (this.firstServiceTop + 85 < -1760 && this.firstServiceTop + 85 > -2370) {
                this.titleH1 = "Haj + Szakáll"
                this.setPrices('hairAndBeard')
                this.lineBg = "#ffc600"
                this.textColor = "#ffc600"
                this.shownSvg = 4
                this.outlinePixels = "2px"
            } else {
                this.titleH1 = "Szolgáltatásaink"
                this.actualPriceDani = 0
                this.actualPriceMark = 0
                this.actualPriceErik = 0
                this.actualPriceMilan = 0
                this.lineBg = "#ffc600"
                this.textColor = "transparent"
                this.shownSvg = 0
                this.outlinePixels = "0px"
            }
        },
        setPrices(serviceType) {
            this.actualPriceDani = this.services[0][serviceType];
            this.actualPriceMark = this.services[1][serviceType];
            this.actualPriceErik = this.services[2][serviceType];
            this.actualPriceMilan = this.services[3][serviceType];
        },
    },
}
</script>

